import { useState } from "react";
import "./Form.css";
import Axios from "axios";
import GetTable from "./GetTable";

function Form() {
  const [lot, setLot] = useStat("");
  const [OF, setOF] = useStat("");
  const [CR, setCR] = useStat("");
  const [CP, setCP] = useStat("");
  const data = { OF: OF, Lot: lot, CR: CR, CP: CP };
  return (
    <div className="containor">
      <form>
        <div className="formCell">
          <div className="field">
            <label>Lot</label>
            <input
              type="text"
              required
              onChange={(e) => setLot(e.target.value)}
            />
          </div>
        </div>
        <div className="formCell">
          <div className="field">
            <label>Ordre de Fabrication</label>
            <input
              type="text"
              required
              onChange={(e) => setOF(e.target.value)}
            />
          </div>
          <div className="field">
            <label>Cantité préparé</label>
            <input
              type="text"
              required
              onChange={(e) => setCP(e.target.value)}
            />
          </div>
        </div>
        <div className="formCell">
          <div className="field">
            <label>Chapm Rebut</label>
            <input
              type="text"
              required
              onChange={(e) => setCR(e.target.value)}
            />
          </div>
          <div className="field">
            <label>Commentaire</label>
            <input type="text" required />
          </div>
        </div>
      </form>
      <GetTable input={data} />
    </div>
  );
}

export default Form;
