import "./Form.css";
import GetTable from "./GetTracTable";
import {
  useDownloadExcel,
  DownloadTableExcel,
} from "react-export-table-to-excel";
import { useRef } from "react";
import { Axios } from "axios";
function Form() {
  const tableRef = useRef(null);


  Axios.get("http://localhost:3001/tracData").then((res) => {
    setData(res.data);
    console.log(res);
  });

useEffect(() => {
  getUsers();
}, []);

const column = [
  { heading: "ID", value: "id" },
  { heading: "Matricule", value: "Matricule" },
  { heading: "User", value: "User" },
  { heading: "Lot", value: "Lot" },
  { heading: "Of", value: "Of" },
  { heading: "Reference", value: "Table" },
  { heading: "Type", value: "Commentaire" },
  { heading: "Qte_Saisi", value: "Qte_Saisi" },
  { heading: "Date d'opération", value: "Date_doperation" },
];

  const { onDownload } = useDownloadExcel({
    currentTableRef: tableRef.current,
    filename: "Users table",
    sheet: "Users",
  });

  return (
    <div className="containor">
      <button onClick={onDownload}> Export excel </button>
      <GetTable />
    </div>
  );
}

export default Form;
