import "./Form.css";
import GetTable from "./GetTracTable";
import {
  useDownloadExcel,
  DownloadTableExcel,
} from "react-export-table-to-excel";
import { useRef } from "react";
function Form() {
  const tableRef = useRef(null);

  const { onDownload } = useDownloadExcel({
    currentTableRef: tableRef.current,
    filename: "Users table",
    sheet: "Users",
  });

  return (
    <div className="containor">
      <button onClick={onDownload}> Export excel </button>

      <table ref={tableRef}>
        <tbody>
          <tr>
            <th>Firstname</th>
            <th>Lastname</th>
            <th>Age</th>
          </tr>
          <tr>
            <td>Edison</td>
            <td>Padilla</td>
            <td>20</td>
          </tr>
          <tr>
            <td>Alberto</td>
            <td>Lopez</td>
            <td>94</td>
          </tr>
        </tbody>
      </table>
      {/* <table ref={tableRef} id="tabletable">
        <GetTable data={data} />
      </table> */}
    </div>
  );
}

export default Form;
