import { useEffect, useState } from "react";
import "./Form.css";
import Axios from "axios";
import GetTable from "./GetTable";

function Form() {
  const [lot, setLot] = useState("");
  const [OF, setOF] = useState("");
  const [CR, setCR] = useState("");
  const [CP, setCP] = useState("");
  const [data, setData] = useState([]);

  const updateData = {
    CR: CR,
    CP: CP,
  };

  

  const getUsers = () => {
    Axios.get("http://localhost:3001/data/new_inter").then((res) => {
      setData(res.data);
      console.log(res);
    });
  };
  useEffect(() => {
    getUsers();
  }, []);


  // const filter = (rows) => {
  //   return rows.filter(
  //     (rows) =>
  //       rows.OF.toString().toLowerCase().indexOf(OF) === 0 &&
  //       rows.Lot.toString().toLowerCase().indexOf(lot) === 0
  //   );
  // };
  return (
    <div className="containor">
      <form>
        <div className="formCell">
          <div className="field">
            <label>Lot</label>
            <input
              type="text"
              required
              onChange={(e) => setLot(e.target.value)}
            />
          </div>
        </div>
        <div className="formCell">
          <div className="field">
            <label>Ordre de Fabrication</label>
            <input
              type="text"
              required
              onChange={(e) => setOF(e.target.value)}
            />
          </div>
          <div className="field">
            <label>Quantité préparé</label>
            <input
              type="text"
              required
              onChange={(e) => setCP(e.target.value)}
            />
          </div>
        </div>
        <div className="formCell">
          <div className="field">
            <label>Champ Rebut</label>
            <input
              type="text"
              required
              onChange={(e) => setCR(e.target.value)}
            />
          </div>
          <div className="field">
            <label>Commentaire</label>
            <input type="text" required />
          </div>
        </div>
      </form>
      <GetTable update={updateData} />
    </div>
  );
}

export default Form;
