import Login from "./Components/Login page/login";
import Interface from "./Components/NavBar/nav";
import { BrowserRouter, Route, Switch } from "react-router-dom";
import { LoginContext } from "./Helper/context";
import { useState } from "react";
import Logout from "./Components/Logout_Header/logout";

import Bloquage from "../Update_Bloquage/Interface";
import Users from "./Components/Form/Form";
import Demontage from "../Update_demontage/Interface";
import Prep from "../Update_QP_QR/Interface";
import Zinguage from "../Update_Zinguage/Interface";
import Montage from "../Update_Montage/Interface";
import Export from "../Update_Export/Interface";
import Upload from "../Upload_file/upload";

function App() {
  const [logedIn, setLogedIn] = useState(false);
  return (
    <BrowserRouter>
      <LoginContext.Provider value={{ logedIn, setLogedIn }}>
        <Logout></Logout>
        <Switch>
          <Route path="/login">
            <Login></Login>
          </Route>
          <Route exact path="/interface">
            <Interface state={logedIn}></Interface>
          </Route>
          <Route path="/interface/upload">
            <Upload></Upload>
          </Route>
          <Route path="/interface/utilisateur">
            <Users></Users>
          </Route>
          <Route path="/demontage">
            <Demontage></Demontage>
          </Route>
          <Route path="/preparation">
            <Prep></Prep>
          </Route>
          <Route path="/bloquage">
            <Bloquage></Bloquage>
          </Route>
          <Route path="/zinguage">
            <Zinguage></Zinguage>
          </Route>
          <Route path="/montage">
            <Montage></Montage>
          </Route>
          <Route path="/export">
            <Export></Export>
          </Route>
        </Switch>
      </LoginContext.Provider>
    </BrowserRouter>
  );
}

export default App;
